<script>
jQuery(document).ready(function ($) {
	var orderSlider = $("#order-slider");
	var sliderOrderValue = $("#slider-order-value #numbers");
	var businessPrice = $("#business-price");
	var ultimatePrice = $("#ultimate-price");
	var businessPriceMobile = $("#business-price-mobile");
	var ultimatePriceMobile = $("#ultimate-price-mobile");

	var prices = {
		1: { orderNumber: 10000, businessPrice: 350, ultimatePrice: 525 },
		2: { orderNumber: 15000,  businessPrice: 475, ultimatePrice: 700 },
		3: { orderNumber: 20000,  businessPrice: 575, ultimatePrice: 875 },
		4: { orderNumber: 30000,  businessPrice: 775, ultimatePrice: 1150 },
		5: { orderNumber: 40000,  businessPrice: 925, ultimatePrice: 1400 },
		6: { orderNumber: 50000,  businessPrice: 1100, ultimatePrice: 1650 },
		7: { orderNumber: 65000,  businessPrice: 1375, ultimatePrice: 2050 },
		8: { orderNumber: 80000,  businessPrice: 1625, ultimatePrice: 2450 },
	};

	function updatePrices(price) {
		if (prices.hasOwnProperty(price)) {
			var priceObj = prices[price];
			var businessPriceValue = priceObj.businessPrice;
			var ultimatePriceValue = priceObj.ultimatePrice;
			var orderValue = priceObj.orderNumber;

			businessPrice.text(businessPriceValue.toLocaleString("en"));
			ultimatePrice.text(ultimatePriceValue.toLocaleString("en"));
			businessPriceMobile.text(businessPriceValue.toLocaleString("en"));
			ultimatePriceMobile.text(ultimatePriceValue.toLocaleString("en"));
			sliderOrderValue.text(orderValue.toLocaleString());
		}
	}

	orderSlider.on("input change", function () {
		var price = parseInt($(this).val());
		updatePrices(price);
	});

	$(function() {
		var windowWidth = $(window).width();

		if (windowWidth > 576) {
			var rangePercent = 0;
			$('[type="range"]').on('change input', function() {
				rangePercent = $('[type="range"]').val() - 1;
				var leftPosition = 13.66 * rangePercent + '%';
				$('#slider-order-value').css({
					'position': 'relative',
					'left': leftPosition
				});
			});
		}
		
		if (windowWidth < 576) {
			var rangePercent = 0;
			$('[type="range"]').on('change input', function() {
				rangePercent = $('[type="range"]').val() - 1;
				var leftPosition = 0 + '%';
				$('#slider-order-value').css({
					'position': 'relative',
					'left': leftPosition
				});
			});
		}
	});
	
	$(window).resize(function() {
	  var windowWidth = $(window).width();

		if (windowWidth < 576) {
			var rangePercent = 0;
			$('[type="range"]').on('change input', function() {
				rangePercent = $('[type="range"]').val() - 1;
				var leftPosition = 0 + '%';
				$('#slider-order-value').css({
					'position': 'relative',
					'left': leftPosition
				});
			});
	  }
	  		if (windowWidth > 576) {
			var rangePercent = 0;
			$('[type="range"]').on('change input', function() {
				rangePercent = $('[type="range"]').val() - 1;
				var leftPosition = 13.66 * rangePercent + '%';
				$('#slider-order-value').css({
					'position': 'relative',
					'left': leftPosition
				});
			});
		}
	});
    
	var swiper = new Swiper('.swiper-container', {
		slidesPerView: 'auto',
		spaceBetween: 20,
	});
	
	$("input[type='range']").each(function() {
		activateInput($(this));
	});

	$(document).on("input", "input[type='range']", function(evt) {
		var input = $(evt.target);
		activateInput(input);
	});

	function activateInput(input) {
		var label = input.parent().find("label");
		label.html(input.val());
		var inputMin = input.attr("min");
		var inputMax = input.attr("max");
		var unidad = (inputMax - inputMin) / 100;
		input.css("--value", (input.val() - inputMin) / unidad);
	}
	
});



</script>
